/**
 * Action generated by user
 */
export interface IAction<T> {
  /** Type of action */
  type: string;
  /** Data passed with action */
  payload: T;
}

/* Payload interfaces */

export interface ItemPayload {
  item: IListItemData;
}

export interface IdPayload {
  id: string;
}


/**
 * List Item Data Model
 */
export interface IListItemData {
  /** Id of item */
  id: string;
  /** Value held by item */
  value: string;
}
